mapper.insert:

	SELECT LPAD(NVL(MAX(bno), 0) + 1, 3,'0')
	FROM tbl_board

INSERT INTO tbl_board(
	bno
	, title
	, contents
	, writer

		, image

)
VALUES(
	#{bno}
	, #{title}


			, #{contents}


			, 'No Contents'


	, #{writer}

		, #{image}

)

mapper.update:
UPDATE tbl_board


		updatedate = sysdate


		updatedate = #{updatedate}


		, writer = #{writer}


		, contents = #{contents}


